<script>
import Vue from "vue";
import { library } from "@fortawesome/fontawesome-svg-core";
import { faComments } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/vue-fontawesome";
library.add(faComments);

Vue.component("font-awesome-icon", FontAwesomeIcon);

export default {
  data() {
    return {
      randomIssue: {},
      clicked: false,
      toggle: false
    };
  },

  props: ["issues", "errors"],

  methods: {
    getRandomIssue: function() {
      this.clicked = true
      this.toggle = !this.toggle
      if (this.errors.length > 0) {
        this.randomIssue = {
          title: "Error! Unable to fetch issues",
          body: "The GitHub API may not be responding. Please refresh shortly."
        };
      } else {
        this.randomIssue = this.issues[
          Math.floor(Math.random() * Math.floor(this.issues.length))
        ]
      }
    }
  }
};
</script>

